mapper.select:
select id, email, name, phone, address, created_at, updated_at, is_deleted, member_id, address_detail
from client
where id = #{clientId} and is_deleted = false;

select id, email, name, phone, address, created_at, updated_at, is_deleted, member_id, address_detail
from client
where email = #{email} and is_deleted = false;

mapper.update:
update client
set member_id = #{memberId}
where id = #{clientId};

update client
set email = #{email}, name = #{name}, phone = #{phone}, address = #{address} , address_detail = #{addressDetail}
where id = #{clientId};

update client
set is_deleted = true
where id = #{clientId};

