mapper.select:
select *
from client
where id = #{clientId} and is_deleted = false;

select *
from client
where email = #{email} and is_deleted = false;

mapper.update:
update client
set member_id = #{memberId}
where id = #{clientId};

update client
set email = #{email}, name = #{name}, phone = #{phone}, address = #{address}
where id = #{clientId};

update client
set is_deleted = true
where id = #{clientId};

mapper.select:
select *
from client
where is_deleted = false;

select clientId
from lawsuit_client_map
where lawsuit_id = #{lawsuitId}
